import React, { useState } from 'react'
import Register from './Register'
import Step1 from './Step1'
import Step2 from './Step2'
import Step3 from './Step3'
import '../assets/startform.scss'
import X from '../assets/Icon-metro-cross-red.png'
function FormStart() {
    const [page, setPage]  = useState(0);
    const [formData, setFormData] = useState({
        license: '',
        name: '',
    });

    const FormTitles = ["Register car", "step 1", "step 2", "step 3"]

    const [showForm, setShowForm] = useState(true)


   function FormModal() {
            showForm = false
      };



    const PageDisplay = () => {
        if (page === 0) {
            return <Register formData={ formData } setFormData={setFormData} />;
        } else if (page === 1){
            return <Step1 />
        } else if (page === 2){
            return <Step2 />
        } else if (page === 3){
            return <Step3 />
        }

    }

  return (
    <div className="start-form back-drop">


            <div className="start-form-container">
                    <div className="progess-start">
            <div style={{width: page === 0 ? "25%" : page === 1 ? "50%" : page === 2 ? "75%" : "100%" }}>
                 </div>
            </div> 

            <img src={X} alt="" onClick={() => FormModal(!showForm)}/>


                <div className="header-start">
                    <h1>{FormTitles[page]}</h1>
                </div>


                <div className="body-start">
                    {PageDisplay()}
                </div>


                <div className="footer-start">
                    <button 
                     disabled ={page === 0 }
                    onClick={() => {setPage((currPage) => currPage - 1)}}>prev</button>
                    <button

                     onClick={() => {
                         if (page === FormTitles.length -1 )
                         {console.log(formData)}


                         setPage((currPage) => currPage + 1)}}>next</button>
                </div>
            </div>



    </div>
  )
}

export default FormStart
